// ESERCIZIO 4
function checkAccess() {
    const addresses = [
        'mymail@mail.com',
        'yourmail@mail.com',
        'hermail@mail.com',
        'hismail@mail.com',
    ];
    const userEmail = prompt('Inserisci il tuo indirizzo email');

    let grantAccess = 'false';

    if (addresses.includes(userEmail)) {
        grantAccess = 'true';
    }

    if (grantAccess === true) {
        console.log('Accesso consentito!');
    } else {
        console.log('Accesso negato!');
    }
}
checkAccess();
//1. Verifica se l'email è registrata e quindi permette l'acceso o meno
//2. true e false non devono essre tra apici perchè non verrebbero presi come valori booleani e di conseguenza il secondo if avvrebbe sempre come risultato il valore dell'else